INSERT INTO bukinfood.restaurante (nombre, descripcion, precio, plan_id, domicilio_id, numero_valoraciones, puntuacion_promedio, politicas, reglas_restaurante, salud_seguridad, menu, hora_apertura, hora_cierre) VALUES
("Astrid y Sabor", "Restaurante de alta cocina peruana", 95000, 1, 1, 2, 5, "Políticas del restaurante Astrid y Sabor", "Reglas del restaurante Astrid y Sabor", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Cebiche de pescado fresco. Lomo saleado con papas doradas. Ají de gallina con arroz blanco. Causa limeña con langostinos. Arroz con mariscos. Anticuchos de pollo. Suspiro limeño","12:00 - 13:00",""),
("Cocina y Cava", "Exquisita cocina de autor", 123000, 1, 2, 2, 5, "Políticas del restaurante Cocina y Cava", "Reglas del restaurante Cocina y Cava", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Risotto de hongos silvestres. Filete de salmón con salsa de cítricos. Carpaccio de res con rúcula y parmesano. Gnocchi en salsa de trufa. Tiramisú casero. Sopa de cebolla gratinada. Salmón a la plancha con vegetales salteados","12:00 - 13:00",""),
("Harry Sabor", "Gastronomía de alta gama en ambiente elegante", 145000, 1, 3, 2, 5, "Políticas del restaurante Harry Sabor", "Reglas del restaurante Harry Sabor", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Entrecot de ternera con chimichurri. Salmón en costra con hierbas. Ravioles rellenos de espinaca y ricotta en salsa de tomate. Mousse de chocolate con frutos rojos. Pulpo a la parrilla con paps confitadas. Escalope de cerdo con salsa de hongos. Tarta de limón merengada","12:00 - 13:00",""),
("Cielos", "Experiencia gastronómica creativa y sensorial", 76000, 1, 4, 2, 5, "Políticas del restaurante Cielos", "Reglas del restaurante Cielos", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Sopa de tomate con crujiente de queso parmesano. RAviolis de langosta en salsa de mariscos. Medallones de ternera en salsa de vino tinto. Coulant de chocolate con helado de vainilla. Capaccio de pulpo con alioli de ají amarillo. Pollo al curry con arroz basmati. Tarta de manzana con helado de canela","12:00 - 13:00",""),
("Andrea Carne de Res", "Famoso restaurante de carnes y fiestas", 98000, 1, 5, 2, 5, "Políticas del restaurante Andrea Carne de Res", "Reglas del restaurante Andrea Carne de Res", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Churrasco de res con chimichurri. Paracón pisao con guacamole y hogao. Empanada de carne. Posta negra con arepas de queso. Arroz con coco y camarones. Chicharrón de cerdo con patacones. Flan de caramelo","12:00 - 13:00",""),
("Rafa Cocina", "Cocina contemporánea con toques colombianos", 115000, 1, 6, 2, 5, "Políticas del restaurante Rafa Cocina", "Reglas del restaurante Rafa Cocina", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Ensalada de quinoa con vegetales asados. Cochinillo confitado con puré de manzana. Risotto de hongos porcini. Tarta de queso con frutos rojos. Ceviche de pulpo y mango. Conejo en salsa de mostaza con papas doradas. Volcan de chocolate con helado de vainilla","12:00 - 13:00",""),
("Criterion", "Platos gourmet en un entorno sofisticado", 82000, 2, 7, 2, 5, "Políticas del restaurante Criterion", "Reglas del restaurante Criterion", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Sopa de mariscos con leche de coco. Cordero al horno con puré de patatas trufado. Gnocchi de espinaca en salda de cuatro quesos. Tarta de frutas frescas . Terrina de foie gras con mermelada de higos. Bacalao confitado con salsa de pimientos asados. Coulant de chocolate blanco con helado de frambuesa","12:00 - 13:00",""),
("Molecular Bogotá", "Cocina molecular y experiencias sorprendentes", 135000, 2, 8, 2, 5, "Políticas del restaurante Molecular Bogotá", "Reglas del restaurante Molecular Bogotá", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Espuma de aguacate con crujiente de camarones. Filete de res en salsa de vino tinto y hongos. Espaguetis al dente con salsa de trufa. ESfera de chocolate rellena de frutos rojos. Cevhiche de pulpo con espuma de limon. Salmon a la parrilla con salsa de maracuyá. Mouse de maracuyá con crocante de almendras","13:00 - 14:00",""),
("80 Sillas", "Sabores auténticos en un ambiente acogedor", 69000, 2, 9, 2, 5, "Políticas del restaurante 80 Sillas", "Reglas del restaurante 80 Sillas", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Arepas rellenas de queso y hogao. Pollo a la brasa con papas fritas. Pescado fritos con arroz con coco. Tarta de queso al horno con salsa de frutos rojos. Ceviche de corvina con maíz chulpe. Lomo de cerdo agridulce con verduras salteadas. Torta de chocolate con arequipe.","13:00 - 14:00",""),
("Central Cevichería", "Ceviches y pescados frescos en estilo peruano", 104000, 2, 10, 2, 5, "Políticas del restaurante Central Cevichería", "Reglas del restaurante Central Cevichería", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Cevhiche mixto con leche de tigre. Jalea de mariscos con salsa criolla. Sudado de pescado con yuca. Mazamorra de camote con helado de vainilla. Causa rellena de pulpo. Tiradito de salmón con salsa de maracuyá. Tarta de maracuyá con merengue","13:00 - 14:00",""),
("Mini-Mal", "Comida saludable y opciones vegetarianas", 77000, 2, 11, 2, 5, "Políticas del restaurante Mini-Mal", "Reglas del restaurante Mini-Mal", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Ensalada de quinoa con vegetales asados. Hamburguesa vegetariana de lentejas y quinoa. Tacos veganos con guacamole y salsa picante. Brownie de chocolate sin gluten. Sopa de miso con tofu y algas. Curry de verduras con arroz basmati. Helado vegano de frutos rojos.","13:00 - 14:00",""),
("Masa", "Pizzas artesanales y deliciosos postres", 87000, 2, 12, 2, 5, "Políticas del restaurante Masa", "Reglas del restaurante Masa", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Pizza margarita con tomates frescos y albahaca. Calzone relleno de jamón y queso. Focaccia con aceite de oliva y romero. Tiramisú de café y mascarone. Ensalada caprese con mozzarella de búfala. Risotto de hongos y parmesano. panna cotta con salsa de frutos rojos.","13:00 - 14:00",""),
("El Chato", "Sabores tradicionales y cócteles innovadores", 116000, 3, 13, 2, 5, "Políticas del restaurante El Chato", "Reglas del restaurante El Chato", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Chicharrón de cerdo con yuca frita. Chuleta de cerdo con puré de manzana. Espaguetis a la cabonara. Tarta de chocolate con salsa de caramelo. Ceviche mixto con leche de tigre. Filete de res con chimichurri. Tarta de limón con merengue","13:00 - 14:00",""),
("El Kiosco", "Comida típica colombiana en ambiente casual", 68000, 3, 14, 2, 5, "Políticas del restaurante El Kiosco", "Reglas del restaurante El Kiosco", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Bandeja paisa con carne, arroz, frijoles, huevo frito y plátano maduro. Sanconcho de gallina con arroz blanco. Empanadas de carne y papa. Posta cartagenera con arepas de huevo. Cevhiche de pescado blanco. Pollo asado con papas criollas. Flan de caramelo","13:00 - 14:00",""),
("Wok", "Delicias asiáticas preparadas al wok", 89000, 3, 15, 2, 4, "Políticas del restaurante Wok", "Reglas del restaurante Wok", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Arroz frito con vegetales y pollo. Tallarines salteados al estilo oriental con camarones. Rollitos de primavera con salsa agridulce. Helado frito con salsa de chocolate. Sopa de fideos de arroz con pollo y vegetales. Pato a la naranja con arroz blanco. Flan de coco","14:00 - 15:00",""),
("Carmen", "Fusión de sabores latinoamericanos", 118000, 3, 16, 2, 4, "Políticas del restaurante Carmen", "Reglas del restaurante Carmen", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Cevhiche de corvina con leche de tigre de ají amarillo. Risotto de quinoa con vegetales asados. Lomo de cerdo a la parrila con puré de camote. Suspiro limeño. Pulpo a la parrilla con chimichurri. Filete de res en salsa de vino tinto con puré de papas. Tarta de chocolate con frutos rojos.","14:00 - 15:00",""),
("El Cielo", "Gastronomía creativa y experiencias sensoriales", 125000, 3, 17, 2, 4, "Políticas del restaurante El Cielo", "Reglas del restaurante El Cielo", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Carpaccio de res con salsa de trufa. Lasaña de langosta con salsa de mariscos. Chuleta de cerdo con salsa de tamarindo. Tiramisú de maracuyá. Causa limeña con camarones. Salmón de costra de quinoa con puré de coliflor. Mousse de chocolate blanco con trufas tropicales","14:00 - 15:00",""),
("Oci.Mde", "Cocina contemporánea en un ambiente elegante", 98000, 3, 18, 2, 4, "Políticas del restaurante Oci.Mde", "Reglas del restaurante Oci.Mde", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Ensalada de rúcula con peras y nueces. Conejo al horno con papas gratinadas. Pasta al pesto con tomates secos. Cheesecake de frutos rojos. Ceviche de pulpo con ají limo. Pollo al curry con arroz basmati. Créme brûlée de vainilla.","14:00 - 15:00",""),
("Mondongos y más", "Platos tradicionales colombianos con un toque moderno", 77000, 4, 19, 2, 4, "Políticas del restaurante Mondongos y más", "Reglas del restaurante Mondongos y más", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Mondongo santafereño con arroz blanco y aguacate. Tamales antioqueños con hogao. Ajiaco bogotano con crema de alcaparras. Natilla con buñuelos. Chicharrón con arepas y hogao. Posta negra de arepas de chócolo. Torta de tres leches.","14:00 - 15:00",""),
("Criterión", "Exquisita gastronomía en un ambiente sofisticado", 135000, 4, 20, 2, 4, "Políticas del restaurante Criterión", "Reglas del restaurante Criterión", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Sopa de cebolla gratinada. Solomillo de ternera con salsa de vino tinto. Raviolis rellenos de ricotta y espinacas en salsa de tomate.","14:00 - 15:00",""),
("El Herbario", "Cocina saludable y opciones vegetarianas", 68000, 4, 21, 2, 4, "Políticas del restaurante El Herbario", "Reglas del restaurante El Herbario", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Ensalada de quinoa con aguacate y tomate. Hamburguesa vegetariana de frijoles negros y remolacha. Rollitos de calabacín rellenos de queso de cabra. Brownie de chocolate sin gluten con helado de coco. Sopa de lentejas con verduras. Curry de garbanzos con arroz basmati. Crumble de manzana con helado de vainilla","14:00 - 15:00",""),
("El Patio del Tango", "Sabores argentinos y shows de tango en vivo", 89000, 4, 22, 2, 4, "Políticas del restaurante El Patio del Tango", "Reglas del restaurante El Patio del Tango", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Bife de chorizo con chimichurri. Empanadas argentinas de carne. Milanesa de ternera con papas fritas. Flan casero con dulce de leche.Provoleta a la parrilla con tomate y albahaca.Lomo de cerdo con salsa de malbec.Panqueques de dulce de leche con helado de vainilla.","12:00 - 13:00",""),
("Barcal", "Platos mediterráneos y una amplia carta de vinos", 114000, 4, 23, 2, 4, "Políticas del restaurante Barcal", "Reglas del restaurante Barcal", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Tabla de embutidos y quesos mediterráneos. Paella mixta con mariscos y pollo. Gazpacho andaluz. Crema catalana. Calamares a la romana con alioli. Pollo al limón con patatas al horno. Tarta de Santiago con almendras","12:00 - 13:00",""),
("Oci.Mde Envigado", "Extensión de Oci.Mde en el municipio de Envigado", 86000, 4, 24, 2, 4, "Políticas del restaurante Oci.Mde Envigado", "Reglas del restaurante Oci.Mde Envigado", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Ensalada mediterránea con queso feta y aceitunas. Paella de mariscos. Gazpacho de sandía. Crema de crema catalana con caramelo. Pulpo a la gallega con patatas al horno. Pollo al ajillo con arroz blanco. Tarta de crema con frutas frescas. ","12:00 - 13:00",""),
("Carmen del Carmen", "Versión más casual y accesible del restaurante Carmen", 72000, 1, 25, 2, 4, "Políticas del restaurante Carmen del Carmen", "Reglas del restaurante Carmen del Carmen", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Arepas de queso y hogao. Hamburguesa de carne con salsa criolla. Pescado frito con patacones. Churros con chocolate caliente. Ceviche mixto con leche de tigre. Lomo saltado con papas fritas. Tarta de limón con merengue. ","13:00 - 14:00",""),
("Café Zorba", "Comida griega auténtica y ambiente acogedor", 99000, 2, 26, 2, 4, "Políticas del restaurante Café Zorba", "Reglas del restaurante Café Zorba", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Gyro de pollo con tzatziki. Souvlaki de cerdo con pita. Moussaka de berenjenas. Baklava con nueces y miel. Ensalada griega con queso feta. Dolmades (hojas de parra rellenas de arroz). Galaktoboureko (pastel de sémola con jarabe de miel). ","13:00 - 14:00",""),
("El Kiosko", "Cocina colombiana tradicional en un ambiente familiar", 73000, 3, 27, 2, 4, "Políticas del restaurante El Kiosko", "Reglas del restaurante El Kiosko", "Se aplican las pautas de distanciamiento social y otras normas relacionadas con el coronavirus, detectores de humo y correcta manipulacion de alimentos.", "Bandeja paisa con carne, arroz, frijoles, huevo frito y plátano maduro. Sancocho de gallina con arroz blanco. Empanadas de carne y papa. Posta cartagenera con arepas de huevo. Ceviche de pescado blanco. Pollo asado con papas criollas. Flan de caramelo.","14:00 - 15:00",""),
